"use client"

import React, { type CSSProperties } from "react"
import type { Header, Cell } from "@tanstack/react-table"
import {
  DndContext,
  KeyboardSensor,
  MouseSensor,
  TouchSensor,
  closestCenter,
  type DragEndEvent,
  type Modifier,
  useSensor,
  useSensors,
} from "@dnd-kit/core"
import { restrictToHorizontalAxis } from "@dnd-kit/modifiers"
import {
  arrayMove,
  SortableContext,
  useSortable,
  horizontalListSortingStrategy,
} from "@dnd-kit/sortable"
import { CSS } from "@dnd-kit/utilities"

import { TableHead, TableCell as TableCellUI } from "@/components/ui/table"
import { cn } from "@/lib/utils"

// ============================================================================
// TableColumnDndProvider
// ============================================================================

export interface TableColumnDndProviderProps {
  children: React.ReactNode
  /** The current column order (array of column IDs) */
  columnOrder: string[]
  /** Callback when columns are reordered. Receives the new column order. */
  onColumnOrderChange: (columnOrder: string[]) => void
  /** DnD modifiers. Defaults to [restrictToHorizontalAxis]. */
  modifiers?: Modifier[]
}

export function TableColumnDndProvider({
  children,
  columnOrder,
  onColumnOrderChange,
  modifiers = [restrictToHorizontalAxis],
}: TableColumnDndProviderProps) {
  const sensors = useSensors(
    useSensor(MouseSensor, {}),
    useSensor(TouchSensor, {}),
    useSensor(KeyboardSensor, {}),
  )

  const handleDragEnd = React.useCallback(
    (event: DragEndEvent) => {
      const { active, over } = event
      if (active && over && active.id !== over.id) {
        const oldIndex = columnOrder.indexOf(active.id as string)
        const newIndex = columnOrder.indexOf(over.id as string)
        onColumnOrderChange(arrayMove(columnOrder, oldIndex, newIndex))
      }
    },
    [columnOrder, onColumnOrderChange],
  )

  return (
    <DndContext
      collisionDetection={closestCenter}
      modifiers={modifiers}
      onDragEnd={handleDragEnd}
      sensors={sensors}
    >
      <SortableContext
        items={columnOrder}
        strategy={horizontalListSortingStrategy}
      >
        {children}
      </SortableContext>
    </DndContext>
  )
}

TableColumnDndProvider.displayName = "TableColumnDndProvider"

// ============================================================================
// TableDraggableHeader
// ============================================================================

export interface TableDraggableHeaderProps<TData, TValue> {
  /** The header instance from TanStack Table */
  header: Header<TData, TValue>
  children: React.ReactNode
  className?: string
}

export function TableDraggableHeader<TData, TValue>({
  header,
  children,
  className,
}: TableDraggableHeaderProps<TData, TValue>) {
  const { attributes, isDragging, listeners, setNodeRef, transform } =
    useSortable({
      id: header.column.id,
    })

  const style: CSSProperties = {
    opacity: isDragging ? 0.8 : 1,
    position: "relative",
    transform: CSS.Translate.toString(transform),
    transition: "width transform 0.2s ease-in-out",
    whiteSpace: "nowrap",
    width: header.column.getSize(),
    zIndex: isDragging ? 1 : 0,
    cursor: "grab",
  }

  return (
    <TableHead
      colSpan={header.colSpan}
      ref={setNodeRef}
      style={style}
      className={cn(isDragging && "bg-muted/50", className)}
      {...attributes}
      {...listeners}
    >
      {children}
    </TableHead>
  )
}

TableDraggableHeader.displayName = "TableDraggableHeader"

// ============================================================================
// TableDragAlongCell
// ============================================================================

export interface TableDragAlongCellProps<TData, TValue> {
  /** The cell instance from TanStack Table */
  cell: Cell<TData, TValue>
  children: React.ReactNode
  className?: string
}

export function TableDragAlongCell<TData, TValue>({
  cell,
  children,
  className,
}: TableDragAlongCellProps<TData, TValue>) {
  const { isDragging, setNodeRef, transform } = useSortable({
    id: cell.column.id,
  })

  const style: CSSProperties = {
    opacity: isDragging ? 0.8 : 1,
    position: "relative",
    transform: CSS.Translate.toString(transform),
    transition: "width transform 0.2s ease-in-out",
    width: cell.column.getSize(),
    zIndex: isDragging ? 1 : 0,
  }

  return (
    <TableCellUI style={style} ref={setNodeRef} className={className}>
      {children}
    </TableCellUI>
  )
}

TableDragAlongCell.displayName = "TableDragAlongCell"

"use client"

import {
  TableColumnDndProvider,
  type TableColumnDndProviderProps,
  TableDraggableHeader,
  type TableDraggableHeaderProps,
  TableDragAlongCell,
  type TableDragAlongCellProps,
} from "../filters/table-column-dnd"

// ============================================================================
// DataTableColumnDndProvider
// ============================================================================

export type DataTableColumnDndProviderProps = Omit<
  TableColumnDndProviderProps,
  never
>

/**
 * Context-aware column DnD provider that wraps children with drag-and-drop
 * column reordering capabilities.
 *
 * @example
 * <DataTableRoot
 *   data={data}
 *   columns={columns}
 *   state={{ columnOrder }}
 *   onColumnOrderChange={setColumnOrder}
 * >
 *   <DataTableColumnDndProvider
 *     columnOrder={columnOrder}
 *     onColumnOrderChange={setColumnOrder}
 *   >
 *     <DataTable>
 *       <DataTableDndHeader />
 *       <DataTableDndColumnBody />
 *     </DataTable>
 *   </DataTableColumnDndProvider>
 * </DataTableRoot>
 */
export function DataTableColumnDndProvider(
  props: DataTableColumnDndProviderProps,
) {
  return <TableColumnDndProvider {...props} />
}

DataTableColumnDndProvider.displayName = "DataTableColumnDndProvider"

// ============================================================================
// DataTableDraggableHeader
// ============================================================================

export type DataTableDraggableHeaderProps<TData, TValue> =
  TableDraggableHeaderProps<TData, TValue>

/**
 * Context-aware draggable header cell for column DnD.
 *
 * @example
 * <DataTableDraggableHeader header={header}>
 *   {flexRender(header.column.columnDef.header, header.getContext())}
 * </DataTableDraggableHeader>
 */
export function DataTableDraggableHeader<TData, TValue>(
  props: DataTableDraggableHeaderProps<TData, TValue>,
) {
  return <TableDraggableHeader<TData, TValue> {...props} />
}

DataTableDraggableHeader.displayName = "DataTableDraggableHeader"

// ============================================================================
// DataTableDragAlongCell
// ============================================================================

export type DataTableDragAlongCellProps<TData, TValue> =
  TableDragAlongCellProps<TData, TValue>

/**
 * Context-aware cell that follows column drag position.
 *
 * @example
 * <DataTableDragAlongCell cell={cell}>
 *   {flexRender(cell.column.columnDef.cell, cell.getContext())}
 * </DataTableDragAlongCell>
 */
export function DataTableDragAlongCell<TData, TValue>(
  props: DataTableDragAlongCellProps<TData, TValue>,
) {
  return <TableDragAlongCell<TData, TValue> {...props} />
}

DataTableDragAlongCell.displayName = "DataTableDragAlongCell"

"use client"

import React from "react"
import { cn } from "@/lib/utils"
import { useDataTable } from "./data-table-context"
import {
  TableHeader,
  TableRow,
  TableBody,
  TableCell,
} from "@/components/ui/table"
import { flexRender } from "@tanstack/react-table"
import { DataTableColumnHeaderRoot } from "../components/data-table-column-header"
import { getCommonPinningStyles } from "../lib/styles"
import {
  TableDraggableRow,
  SortableContext,
  verticalListSortingStrategy,
  type UniqueIdentifier,
} from "../filters/table-row-dnd"
import {
  TableDraggableHeader,
  TableDragAlongCell,
} from "../filters/table-column-dnd"

// ============================================================================
// DataTableDndBody (Row DnD)
// ============================================================================

export interface DataTableDndBodyProps<TData> {
  children?: React.ReactNode
  className?: string
  onRowClick?: (row: TData) => void
}

/**
 * DnD-aware table body that renders rows as draggable items.
 * Drop-in replacement for DataTableBody when using row drag-and-drop.
 *
 * Must be wrapped in a DataTableRowDndProvider (or TableRowDndProvider).
 *
 * @example
 * <DataTableRowDndProvider data={data} onReorder={setData}>
 *   <DataTable>
 *     <DataTableHeader />
 *     <DataTableDndBody />
 *   </DataTable>
 * </DataTableRowDndProvider>
 */
export function DataTableDndBody<TData>({
  children,
  className,
  onRowClick,
}: DataTableDndBodyProps<TData>) {
  const { table, isLoading } = useDataTable<TData>()
  const { rows } = table.getRowModel()

  const dataIds = React.useMemo<UniqueIdentifier[]>(
    () => rows.map(row => row.id),
    [rows],
  )

  return (
    <TableBody className={className}>
      {!isLoading && rows?.length ? (
        <SortableContext items={dataIds} strategy={verticalListSortingStrategy}>
          {rows.map(row => {
            const isClickable = !!onRowClick
            const isExpanded = row.getIsExpanded()

            // Find if any column has expandedContent meta
            const expandColumn = row
              .getAllCells()
              .find(cell => cell.column.columnDef.meta?.expandedContent)

            return (
              <React.Fragment key={row.id}>
                <TableDraggableRow row={row}>
                  {row.getVisibleCells().map(cell => {
                    const size = cell.column.columnDef.size
                    const cellStyle = {
                      width: size ? `${size}px` : undefined,
                      ...getCommonPinningStyles(cell.column, false),
                    }

                    return (
                      <TableCell
                        key={cell.id}
                        style={cellStyle}
                        onClick={
                          isClickable
                            ? event => {
                                const target = event.target as HTMLElement
                                const isInteractiveElement =
                                  target.closest("button") ||
                                  target.closest("input") ||
                                  target.closest("a") ||
                                  target.closest('[role="button"]') ||
                                  target.closest('[role="checkbox"]') ||
                                  target.closest(
                                    "[data-radix-collection-item]",
                                  ) ||
                                  target.closest('[data-slot="checkbox"]') ||
                                  target.tagName === "INPUT" ||
                                  target.tagName === "BUTTON" ||
                                  target.tagName === "A"

                                if (!isInteractiveElement) {
                                  onRowClick?.(row.original)
                                }
                              }
                            : undefined
                        }
                        className={cn(
                          isClickable && "cursor-pointer",
                          cell.column.getIsPinned() &&
                            "bg-background group-hover:bg-muted/50 group-data-[state=selected]:bg-muted",
                        )}
                      >
                        {flexRender(
                          cell.column.columnDef.cell,
                          cell.getContext(),
                        )}
                      </TableCell>
                    )
                  })}
                </TableDraggableRow>

                {/* Expanded content row */}
                {isExpanded && expandColumn && (
                  <TableRow>
                    <TableCell
                      colSpan={row.getVisibleCells().length}
                      className="p-0"
                    >
                      {expandColumn.column.columnDef.meta?.expandedContent?.(
                        row.original,
                      )}
                    </TableCell>
                  </TableRow>
                )}
              </React.Fragment>
            )
          })}
        </SortableContext>
      ) : null}

      {children}
    </TableBody>
  )
}

DataTableDndBody.displayName = "DataTableDndBody"

// ============================================================================
// DataTableDndHeader (Column DnD)
// ============================================================================

export interface DataTableDndHeaderProps {
  className?: string
  /**
   * Makes the header sticky at the top when scrolling.
   * @default true
   */
  sticky?: boolean
}

/**
 * DnD-aware table header that renders column headers as draggable items.
 * Drop-in replacement for DataTableHeader when using column drag-and-drop.
 *
 * Must be wrapped in a DataTableColumnDndProvider (or TableColumnDndProvider).
 *
 * @example
 * <DataTableColumnDndProvider columnOrder={columnOrder} onColumnOrderChange={setColumnOrder}>
 *   <DataTable>
 *     <DataTableDndHeader />
 *     <DataTableDndColumnBody />
 *   </DataTable>
 * </DataTableColumnDndProvider>
 */
export const DataTableDndHeader = React.memo(function DataTableDndHeader({
  className,
  sticky = true,
}: DataTableDndHeaderProps) {
  const { table } = useDataTable()

  const headerGroups = table?.getHeaderGroups() ?? []

  if (headerGroups.length === 0) {
    return null
  }

  return (
    <TableHeader
      className={cn(
        sticky && "sticky top-0 z-10 bg-background",
        sticky &&
          "after:absolute after:right-0 after:bottom-0 after:left-0 after:h-px after:bg-border",
        className,
      )}
    >
      {headerGroups.map(headerGroup => (
        <TableRow key={headerGroup.id}>
          {headerGroup.headers.map(header => (
            <TableDraggableHeader key={header.id} header={header}>
              {header.isPlaceholder ? null : (
                <DataTableColumnHeaderRoot column={header.column}>
                  {flexRender(
                    header.column.columnDef.header,
                    header.getContext(),
                  )}
                </DataTableColumnHeaderRoot>
              )}
            </TableDraggableHeader>
          ))}
        </TableRow>
      ))}
    </TableHeader>
  )
})

DataTableDndHeader.displayName = "DataTableDndHeader"

// ============================================================================
// DataTableDndColumnBody (Column DnD)
// ============================================================================

export interface DataTableDndColumnBodyProps<TData> {
  children?: React.ReactNode
  className?: string
  onRowClick?: (row: TData) => void
}

/**
 * DnD-aware table body for column drag-and-drop.
 * Each cell is wrapped with useSortable to follow column drag position.
 *
 * Must be wrapped in a DataTableColumnDndProvider (or TableColumnDndProvider).
 *
 * @example
 * <DataTableColumnDndProvider columnOrder={columnOrder} onColumnOrderChange={setColumnOrder}>
 *   <DataTable>
 *     <DataTableDndHeader />
 *     <DataTableDndColumnBody />
 *   </DataTable>
 * </DataTableColumnDndProvider>
 */
export function DataTableDndColumnBody<TData>({
  children,
  className,
  onRowClick,
}: DataTableDndColumnBodyProps<TData>) {
  const { table, isLoading } = useDataTable<TData>()
  const { rows } = table.getRowModel()

  return (
    <TableBody className={className}>
      {!isLoading && rows?.length
        ? rows.map(row => {
            const isClickable = !!onRowClick

            return (
              <TableRow
                key={row.id}
                data-row-index={row?.index}
                data-row-id={row?.id}
                data-state={row.getIsSelected() && "selected"}
                className={cn(isClickable && "cursor-pointer", "group")}
              >
                {row.getVisibleCells().map(cell => (
                  <TableDragAlongCell key={cell.id} cell={cell}>
                    {flexRender(cell.column.columnDef.cell, cell.getContext())}
                  </TableDragAlongCell>
                ))}
              </TableRow>
            )
          })
        : null}

      {children}
    </TableBody>
  )
}

DataTableDndColumnBody.displayName = "DataTableDndColumnBody"

"use client"

import React from "react"
import { useVirtualizer } from "@tanstack/react-virtual"
import { flexRender } from "@tanstack/react-table"
import { cn } from "@/lib/utils"
import { useDataTable } from "./data-table-context"
import {
  TableHeader,
  TableRow,
  TableBody,
  TableCell,
} from "@/components/ui/table"
import { DataTableColumnHeaderRoot } from "../components/data-table-column-header"
import { getCommonPinningStyles } from "../lib/styles"
import {
  SortableContext,
  verticalListSortingStrategy,
  type UniqueIdentifier,
} from "../filters/table-row-dnd"
import {
  TableDraggableHeader,
  TableDragAlongCell,
} from "../filters/table-column-dnd"
import { useSortable } from "@dnd-kit/sortable"
import { CSS } from "@dnd-kit/utilities"
import type { ScrollEvent } from "./data-table-virtualized-structure"

// ============================================================================
// VirtualizedDraggableRow â€” internal row component for virtualized DnD
// ============================================================================

interface VirtualizedDraggableRowProps {
  children: React.ReactNode
  rowId: string
  className?: string
}

function VirtualizedDraggableRow({
  children,
  rowId,
  className,
}: VirtualizedDraggableRowProps) {
  const { transform, transition, setNodeRef, isDragging } = useSortable({
    id: rowId,
  })

  const style: React.CSSProperties = {
    transform: CSS.Transform.toString(transform),
    transition: transition,
    opacity: isDragging ? 0.8 : 1,
    zIndex: isDragging ? 1 : 0,
    position: "relative",
  }

  return (
    <TableRow
      ref={setNodeRef}
      style={style}
      className={cn("flex w-full", isDragging && "bg-muted/50", className)}
    >
      {children}
    </TableRow>
  )
}

// ============================================================================
// DataTableVirtualizedDndBody (Row DnD + Virtualization)
// ============================================================================

export interface DataTableVirtualizedDndBodyProps<TData> {
  children?: React.ReactNode
  estimateSize?: number
  overscan?: number
  className?: string
  onScroll?: (event: ScrollEvent) => void
  onScrolledTop?: () => void
  onScrolledBottom?: () => void
  scrollThreshold?: number
  onRowClick?: (
    row: TData,
    event: React.MouseEvent<HTMLTableRowElement>,
  ) => void
}

/**
 * Virtualized DnD-aware table body that combines row virtualization with
 * drag-and-drop reordering. Uses @tanstack/react-virtual for performance
 * and @dnd-kit/sortable for drag interactions.
 *
 * Must be wrapped in a DataTableRowDndProvider (or TableRowDndProvider).
 *
 * @example
 * <DataTableRowDndProvider data={data} onReorder={setData}>
 *   <DataTable height={400}>
 *     <DataTableVirtualizedHeader />
 *     <DataTableVirtualizedDndBody estimateSize={34} overscan={10} />
 *   </DataTable>
 * </DataTableRowDndProvider>
 */
export function DataTableVirtualizedDndBody<TData>({
  children,
  estimateSize = 34,
  overscan = 20,
  className,
  onScroll,
  onRowClick,
  onScrolledTop,
  onScrolledBottom,
  scrollThreshold = 50,
}: DataTableVirtualizedDndBodyProps<TData>) {
  const { table } = useDataTable()
  const { rows } = table.getRowModel()
  const [scrollElement, setScrollElement] =
    React.useState<HTMLDivElement | null>(null)

  const parentRef = React.useCallback(
    (node: HTMLTableSectionElement | null) => {
      if (node !== null) {
        const container = node.closest(
          '[data-slot="table-container"]',
        ) as HTMLDivElement | null
        setScrollElement(container)
      }
    },
    [],
  )

  const rowVirtualizer = useVirtualizer({
    count: rows.length,
    getScrollElement: () => scrollElement,
    estimateSize: () => estimateSize,
    overscan,
    enabled: !!scrollElement,
    measureElement:
      typeof window !== "undefined" &&
      navigator.userAgent.indexOf("Firefox") === -1
        ? element => element?.getBoundingClientRect().height
        : undefined,
  })

  const handleScrollTop = React.useCallback(() => {
    onScrolledTop?.()
  }, [onScrolledTop])

  const handleScrollBottom = React.useCallback(() => {
    onScrolledBottom?.()
  }, [onScrolledBottom])

  React.useEffect(() => {
    if (!scrollElement || !onScroll) return

    const handleScroll = (event: Event) => {
      const element = event.currentTarget as HTMLDivElement
      const { scrollHeight, scrollTop, clientHeight } = element

      const isTop = scrollTop === 0
      const isBottom = scrollHeight - scrollTop - clientHeight < scrollThreshold
      const percentage =
        scrollHeight - clientHeight > 0
          ? (scrollTop / (scrollHeight - clientHeight)) * 100
          : 0

      onScroll({
        scrollTop,
        scrollHeight,
        clientHeight,
        isTop,
        isBottom,
        percentage,
      })

      if (isTop) handleScrollTop()
      if (isBottom) handleScrollBottom()
    }

    scrollElement.addEventListener("scroll", handleScroll, { passive: true })
    return () => scrollElement.removeEventListener("scroll", handleScroll)
  }, [
    scrollElement,
    onScroll,
    handleScrollTop,
    handleScrollBottom,
    scrollThreshold,
  ])

  const dataIds = React.useMemo<UniqueIdentifier[]>(
    () => rows.map(row => row.id),
    [rows],
  )

  const virtualItems = rowVirtualizer.getVirtualItems()
  const hasVirtualItems = virtualItems.length > 0

  const topSpacerHeight = hasVirtualItems ? virtualItems[0].start : 0
  const lastItem = hasVirtualItems
    ? virtualItems[virtualItems.length - 1]
    : null
  const bottomSpacerHeight = lastItem
    ? rowVirtualizer.getTotalSize() - lastItem.end
    : 0

  return (
    <TableBody ref={parentRef} className={cn("block", className)}>
      <SortableContext items={dataIds} strategy={verticalListSortingStrategy}>
        {/* Top spacer for virtual scrolling offset */}
        {topSpacerHeight > 0 && (
          <TableRow
            style={{ height: `${topSpacerHeight}px`, display: "block" }}
          />
        )}

        {/* Render visible rows as draggable */}
        {virtualItems.map(virtualRow => {
          const row = rows[virtualRow.index]
          const isClickable = !!onRowClick
          const isExpanded = row.getIsExpanded()

          const expandColumn = row
            .getAllCells()
            .find(cell => cell.column.columnDef.meta?.expandedContent)

          return (
            <React.Fragment key={`${row.id}-${isExpanded}`}>
              <VirtualizedDraggableRow rowId={row.id}>
                {row.getVisibleCells().map(cell => {
                  const size = cell.column.columnDef.size
                  const cellStyle = {
                    width: size ? `${size}px` : undefined,
                    minHeight: `${estimateSize}px`,
                    ...getCommonPinningStyles(cell.column, false),
                  }

                  return (
                    <TableCell
                      key={cell.id}
                      className={cn(
                        size ? "" : "w-full",
                        "flex items-center",
                        isClickable && "cursor-pointer",
                        cell.column.getIsPinned() &&
                          "bg-background group-hover:bg-muted/50 group-data-[state=selected]:bg-muted",
                      )}
                      style={cellStyle}
                      onClick={
                        isClickable
                          ? event => {
                              const target = event.target as HTMLElement
                              const isInteractiveElement =
                                target.closest("button") ||
                                target.closest("input") ||
                                target.closest("a") ||
                                target.closest('[role="button"]') ||
                                target.closest('[role="checkbox"]') ||
                                target.closest(
                                  "[data-radix-collection-item]",
                                ) ||
                                target.closest('[data-slot="checkbox"]') ||
                                target.tagName === "INPUT" ||
                                target.tagName === "BUTTON" ||
                                target.tagName === "A"

                              if (!isInteractiveElement) {
                                onRowClick(
                                  row.original as TData,
                                  event as unknown as React.MouseEvent<HTMLTableRowElement>,
                                )
                              }
                            }
                          : undefined
                      }
                    >
                      {flexRender(
                        cell.column.columnDef.cell,
                        cell.getContext(),
                      )}
                    </TableCell>
                  )
                })}
              </VirtualizedDraggableRow>

              {/* Expanded content row */}
              {isExpanded && expandColumn && (
                <TableRow className="flex w-full">
                  <TableCell
                    colSpan={row.getVisibleCells().length}
                    className="w-full p-0"
                  >
                    {expandColumn.column.columnDef.meta?.expandedContent?.(
                      row.original,
                    )}
                  </TableCell>
                </TableRow>
              )}
            </React.Fragment>
          )
        })}

        {/* Bottom spacer for remaining virtual height */}
        {bottomSpacerHeight > 0 && (
          <TableRow
            style={{ height: `${bottomSpacerHeight}px`, display: "block" }}
          />
        )}
      </SortableContext>

      {/* Empty state and other children */}
      {children}
    </TableBody>
  )
}

DataTableVirtualizedDndBody.displayName = "DataTableVirtualizedDndBody"

// ============================================================================
// DataTableVirtualizedDndHeader (Column DnD + Virtualization)
// ============================================================================

export interface DataTableVirtualizedDndHeaderProps {
  className?: string
  /**
   * Makes the header sticky at the top when scrolling.
   * @default true
   */
  sticky?: boolean
}

/**
 * Virtualized DnD-aware table header for column drag-and-drop.
 * Uses flex layout matching the virtualized table structure.
 *
 * Must be wrapped in a DataTableColumnDndProvider (or TableColumnDndProvider).
 *
 * @example
 * <DataTableColumnDndProvider columnOrder={columnOrder} onColumnOrderChange={setColumnOrder}>
 *   <DataTable height={400}>
 *     <DataTableVirtualizedDndHeader />
 *     <DataTableVirtualizedDndColumnBody estimateSize={34} />
 *   </DataTable>
 * </DataTableColumnDndProvider>
 */
export const DataTableVirtualizedDndHeader = React.memo(
  function DataTableVirtualizedDndHeader({
    className,
    sticky = true,
  }: DataTableVirtualizedDndHeaderProps) {
    const { table } = useDataTable()

    const headerGroups = table?.getHeaderGroups() ?? []

    if (headerGroups.length === 0) {
      return null
    }

    return (
      <TableHeader
        className={cn(
          "block",
          sticky && "sticky top-0 z-10 bg-background",
          className,
        )}
      >
        {headerGroups.map(headerGroup => (
          <TableRow key={headerGroup.id} className="flex w-full border-b">
            {headerGroup.headers.map(header => {
              const size = header.column.columnDef.size

              return (
                <TableDraggableHeader key={header.id} header={header}>
                  <div
                    className={cn(size ? "" : "w-full", "flex items-center")}
                    style={{
                      width: size ? `${size}px` : undefined,
                      ...getCommonPinningStyles(header.column, true),
                    }}
                  >
                    {header.isPlaceholder ? null : (
                      <DataTableColumnHeaderRoot column={header.column}>
                        {flexRender(
                          header.column.columnDef.header,
                          header.getContext(),
                        )}
                      </DataTableColumnHeaderRoot>
                    )}
                  </div>
                </TableDraggableHeader>
              )
            })}
          </TableRow>
        ))}
      </TableHeader>
    )
  },
)

DataTableVirtualizedDndHeader.displayName = "DataTableVirtualizedDndHeader"

// ============================================================================
// DataTableVirtualizedDndColumnBody (Column DnD + Virtualization)
// ============================================================================

export interface DataTableVirtualizedDndColumnBodyProps<TData> {
  children?: React.ReactNode
  estimateSize?: number
  overscan?: number
  className?: string
  onScroll?: (event: ScrollEvent) => void
  onScrolledTop?: () => void
  onScrolledBottom?: () => void
  scrollThreshold?: number
  onRowClick?: (
    row: TData,
    event: React.MouseEvent<HTMLTableRowElement>,
  ) => void
}

/**
 * Virtualized DnD-aware table body for column drag-and-drop.
 * Each cell follows column drag position using useSortable.
 *
 * Must be wrapped in a DataTableColumnDndProvider (or TableColumnDndProvider).
 *
 * @example
 * <DataTableColumnDndProvider columnOrder={columnOrder} onColumnOrderChange={setColumnOrder}>
 *   <DataTable height={400}>
 *     <DataTableVirtualizedDndHeader />
 *     <DataTableVirtualizedDndColumnBody estimateSize={34} />
 *   </DataTable>
 * </DataTableColumnDndProvider>
 */
export function DataTableVirtualizedDndColumnBody<TData>({
  children,
  estimateSize = 34,
  overscan = 20,
  className,
  onScroll,
  onRowClick,
  onScrolledTop,
  onScrolledBottom,
  scrollThreshold = 50,
}: DataTableVirtualizedDndColumnBodyProps<TData>) {
  const { table } = useDataTable()
  const { rows } = table.getRowModel()
  const [scrollElement, setScrollElement] =
    React.useState<HTMLDivElement | null>(null)

  const parentRef = React.useCallback(
    (node: HTMLTableSectionElement | null) => {
      if (node !== null) {
        const container = node.closest(
          '[data-slot="table-container"]',
        ) as HTMLDivElement | null
        setScrollElement(container)
      }
    },
    [],
  )

  const rowVirtualizer = useVirtualizer({
    count: rows.length,
    getScrollElement: () => scrollElement,
    estimateSize: () => estimateSize,
    overscan,
    enabled: !!scrollElement,
    measureElement:
      typeof window !== "undefined" &&
      navigator.userAgent.indexOf("Firefox") === -1
        ? element => element?.getBoundingClientRect().height
        : undefined,
  })

  const handleScrollTop = React.useCallback(() => {
    onScrolledTop?.()
  }, [onScrolledTop])

  const handleScrollBottom = React.useCallback(() => {
    onScrolledBottom?.()
  }, [onScrolledBottom])

  React.useEffect(() => {
    if (!scrollElement || !onScroll) return

    const handleScroll = (event: Event) => {
      const element = event.currentTarget as HTMLDivElement
      const { scrollHeight, scrollTop, clientHeight } = element

      const isTop = scrollTop === 0
      const isBottom = scrollHeight - scrollTop - clientHeight < scrollThreshold
      const percentage =
        scrollHeight - clientHeight > 0
          ? (scrollTop / (scrollHeight - clientHeight)) * 100
          : 0

      onScroll({
        scrollTop,
        scrollHeight,
        clientHeight,
        isTop,
        isBottom,
        percentage,
      })

      if (isTop) handleScrollTop()
      if (isBottom) handleScrollBottom()
    }

    scrollElement.addEventListener("scroll", handleScroll, { passive: true })
    return () => scrollElement.removeEventListener("scroll", handleScroll)
  }, [
    scrollElement,
    onScroll,
    handleScrollTop,
    handleScrollBottom,
    scrollThreshold,
  ])

  const virtualItems = rowVirtualizer.getVirtualItems()
  const hasVirtualItems = virtualItems.length > 0

  const topSpacerHeight = hasVirtualItems ? virtualItems[0].start : 0
  const lastItem = hasVirtualItems
    ? virtualItems[virtualItems.length - 1]
    : null
  const bottomSpacerHeight = lastItem
    ? rowVirtualizer.getTotalSize() - lastItem.end
    : 0

  return (
    <TableBody ref={parentRef} className={cn("block", className)}>
      {/* Top spacer for virtual scrolling offset */}
      {topSpacerHeight > 0 && (
        <TableRow
          style={{ height: `${topSpacerHeight}px`, display: "block" }}
        />
      )}

      {/* Render visible rows with drag-along cells */}
      {virtualItems.map(virtualRow => {
        const row = rows[virtualRow.index]
        const isClickable = !!onRowClick

        return (
          <TableRow
            key={row.id}
            ref={node => {
              if (node) {
                rowVirtualizer.measureElement(node)
              }
            }}
            data-index={virtualRow.index}
            data-row-index={row?.index}
            data-row-id={row?.id}
            data-state={row.getIsSelected() && "selected"}
            className={cn("group flex w-full", isClickable && "cursor-pointer")}
          >
            {row.getVisibleCells().map(cell => (
              <TableDragAlongCell key={cell.id} cell={cell}>
                <div
                  className={cn(
                    cell.column.columnDef.size ? "" : "w-full",
                    "flex items-center",
                  )}
                  style={{
                    minHeight: `${estimateSize}px`,
                  }}
                >
                  {flexRender(cell.column.columnDef.cell, cell.getContext())}
                </div>
              </TableDragAlongCell>
            ))}
          </TableRow>
        )
      })}

      {/* Bottom spacer for remaining virtual height */}
      {bottomSpacerHeight > 0 && (
        <TableRow
          style={{ height: `${bottomSpacerHeight}px`, display: "block" }}
        />
      )}

      {/* Empty state and other children */}
      {children}
    </TableBody>
  )
}

DataTableVirtualizedDndColumnBody.displayName =
  "DataTableVirtualizedDndColumnBody"
